const data = $input.item.json;

const html = `
<!DOCTYPE html>
<html lang="de">
<head>
<meta charset="UTF-8">
<style>
  body { font-family: 'Helvetica', sans-serif; padding: 40px; color: #333; }
  .container { border: 1px solid #eee; padding: 30px; border-radius: 8px; }
  .header { border-bottom: 2px solid #3e5eba; padding-bottom: 10px; margin-bottom: 20px; }
  .license-box { background: #f9f9f9; padding: 15px; border-radius: 4px; font-family: monospace; font-size: 1.2em; }
  .footer { margin-top: 50px; font-size: 0.8em; color: #777; }
</style>
</head>
<body>
  <div class="container">
    <div class="header"><h1>Lizenzbestätigung</h1></div>
    <p>Hallo <strong>${data.customer_name}</strong>,</p>
    <p>vielen Dank für Ihr Vertrauen. Hiermit erhalten Sie Ihre offizielle Lizenz:</p>
    <div class="license-box">${data.license_key}</div>
    <p>Gültig bis: ${data.expiry_date || 'Unbegrenzt'}</p>
    <div class="footer">Generiert am ${new Date().toLocaleDateString('de-DE')}</div>
  </div>
</body>
</html>
`;

return { 
  html: html, 
  fileName: `Lizenz_${data.id || 'export'}.pdf` 
};
